Example implementation:
```dart
class StringUtils {
  static bool isEmail(String input) {
    final emailRegex = RegExp(r'^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$');
    return emailRegex.hasMatch(input);
  }

  static bool isPhoneNumber(String input, {String? countryCode}) {
    // Implementation based on country code
  }

  static bool isUrl(String input) {
    final urlRegex = RegExp(r'^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})([\/\w \.-]*)*\/?$');
    return urlRegex.hasMatch(input);
  }

  static String capitalize(String input) {
    if (input.isEmpty) return input;
    return input[0].toUpperCase() + input.substring(1);
  }

  static String truncate(String input, int maxLength, {String suffix = '...'}) {
    if (input.length <= maxLength) return input;
    return input.substring(0, maxLength - suffix.length) + suffix;
  }

  static String mask(String input, {int visibleChars = 4, String maskChar = '*'}) {
    if (input.length <= visibleChars) return input;
    final visible = input.substring(input.length - visibleChars);
    final masked = maskChar * (input.length - visibleChars);
    return masked + visible;
  }

  // Additional utility methods
}
